mapper.select:
SELECT DP_NY, DP_NX, T_NY1, T_NX1, T_NY2, T_NX2, T_NY3, T_NX3, EP_NY, EP_NX
FROM MY_ROUTE
WHERE TITLE = #{title}

mapper.update:
UPDATE MY_ROUTE
SET HIT = HIT+1
WHERE MR_ID = #{MR_ID}

update my_route m
set m.reply_count = (select count(mr_no) from mr_reply where mr_bno = #{mr_bno})
where m.mr_id = #{mr_id}

mapper.select:
SELECT HEARTID, MR_ID, USERID, HEART
FROM HEART
WHERE MR_ID = #{mr_id} and USERID = #{userId}

mapper.update:
UPDATE my_route
SET  like_count = like_count+1
WHERE MR_ID =#{mr_id}

UPDATE my_route
SET  like_count = like_count - 1
WHERE MR_ID = #{mr_id}
